import "./styles.css";
import React, { useState } from "react";

export default function App() {
  const [valueEmoji, setValueEmoji] = useState("");
  const [nameEmoji, setNameEmoji] = useState("");
  const emojiArray = [
    {
      emojiName: "smiling",
      emojiDes: "üòä"
    },
    {
      emojiName: "grinning face",
      emojiDes: "üòÄ"
    },
    {
      emojiName: "sad",
      emojiDes: "üòî"
    },
    {
      emojiName: "rolling laughing",
      emojiDes: "ü§£"
    },
    {
      emojiName: "love",
      emojiDes: "‚ù§Ô∏è"
    },
    {
      emojiName: "annoyance",
      emojiDes: "üòë"
    },
    {
      emojiName: "sunglasses",
      emojiDes: "üòé"
    },
    {
      emojiName: "face blowing a kiss",
      emojiDes: "üòò"
    },
    {
      emojiName: "money mouth face",
      emojiDes: "ü§ë"
    },
    {
      emojiName: "heart eyes",
      emojiDes: "üòç"
    }
  ];

  return (
    <div className="App">
      <h3>
        Hello Emoji{" "}
        <span role="img" aria-label="love_emoji">
          üòç
        </span>
      </h3>
      <input
        value={valueEmoji}
        onChange={(e) => {
          setNameEmoji("");
          return setValueEmoji(e.target.value);
        }}
        placeholder="Find Your Emoji"
      />
      <h4>{nameEmoji}</h4>
      <div
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          flexDirection: "row",
          flexWrap: "wrap"
        }}
      >
        {emojiArray &&
          emojiArray
            .filter((emoji) =>
              valueEmoji === ""
                ? emoji
                : emoji.emojiName
                    .toLowerCase()
                    .includes(valueEmoji.toLowerCase())
            )
            .map((e, index) => (
              <div key={index}>
                <div
                  style={{
                    width: "100px",
                    height: "130px",
                    background: "yellow",
                    padding: "10px",
                    margin: "10px",
                    cursor: "pointer"
                  }}
                  onClick={() => {
                    setNameEmoji(e.emojiName);
                  }}
                >
                  <h3>{e.emojiName}</h3>
                  <span role="img" aria-label="image emoji">
                    {e.emojiDes}
                  </span>
                </div>
              </div>
            ))}
      </div>
    </div>
  );
}

